mapper.select:
SELECT b.id, b.title, b.view_count as viewCount, u.name as writer from tb_board AS b LEFT JOIN tb_user AS u ON b.user_id = u.id
WHERE b.title LIKE CONCAT('%', #{titleSearch,jdbcType=VARCHAR},'%')
ORDER BY b.id DESC LIMIT #{startPageNum,jdbcType=INTEGER}, #{perPageNum,jdbcType=INTEGER}

select id, title, content, view_count, date_time, user_id
from tb_board
where id = #{id,jdbcType=INTEGER}

mapper.update:
UPDATE tb_board
SET title=#{title,jdbcType=VARCHAR}, content=#{content,jdbcType=VARCHAR}, date_time = NOW(), view_count=#{viewCount,jdbcType=INTEGER}
WHERE id = #{id,jdbcType=INTEGER}
