mapper.select:
select id, board_id as boardId, comment, user_id as userId
from tb_comment
where id = #{id, jdbcType=INTEGER}

mapper.update:
UPDATE tb_comment
SET COMMENT = #{comment,jdbcType=VARCHAR}
WHERE id = #{id, jdbcType=INTEGER}

mapper.delete:
delete
from tb_comment
where id = #{id, jdbcType=INTEGER}
